# Automatically assign roles based on user email pattern or context
def assign_role_and_links(doc, method):
    import frappe

    # Sample logic to assign roles based on naming conventions or linked fields
    if "educator" in (doc.email or "").lower():
        role = "Educator Admin"
    elif "hr" in (doc.email or "").lower():
        role = "HR Manager"
    elif "team" in (doc.email or "").lower():
        role = "HR Team Manager"
    elif "instructor" in (doc.email or "").lower():
        role = "Instructor"
    else:
        role = "Student"

    # Assign Role
    if not frappe.db.exists("Has Role", {"parent": doc.name, "role": role}):
        doc.add_roles(role)
        frappe.db.commit()

frappe.get_doc("Server Script", "Auto Assign Roles on User Creation").execute = assign_role_and_links
